import React, { Component } from 'react'
import styled from 'styled-components'
import Event from './Event'

const TimelineContainer = styled.section`
  display: flex;
  flex-direction: row;
  &.verticalList {
    flex-direction: column;
  }
`

const changeOrientation = () => {
  console.log('clicked');
  this.setState(
    prevState => ({
      isHorizontal: !prevState.isHorizontal
    })
  );
}

export default class Timeline extends Component {
  constructor(props) {
    super(props)

    this.state= {
      isHorizontal: true
    }
  }

  render() {
    return (
      <TimelineContainer className={this.state.isHorizontal ? '' : 'verticalList'}>
        <Event title="Home" onClick={this.changeOrientation}/>
        <Event title="About" onClick={this.changeOrientation}/>
        <Event title="Contact" onClick={this.changeOrientation}/>
      </TimelineContainer>
    )
  }
}
